// Ensure we're using the platform's node
require('/usr/node/node_modules/platform_node_version').assert();

var cp         = require('child_process');
var execFile   = cp.execFile;
var os         = require('os');

/*
 * Example:
 *
 * onlyif.rootInSmartosGlobal(function(err) {
 *     if (err) {
 *         console.log('Fatal: cannot run because: ' + err);
 *         process.exit(1);
 *     }
 *     console.log('hello root in the GZ!');
 * });
 *
 */

exports.rootInSmartosGlobal = function (callback)
{
    if (process.getuid() !== 0) {
        callback('you are not root.');
        return;
    }

    if (os.type() !== 'SunOS') {
        callback('this is not SmartOS.');
        return;
    }

    execFile('/usr/bin/zonename', [], function (err, stdout, stderr) {
        var zonename;
        if (err) {
            callback('unable to run /usr/bin/zonename.');
            return;
        }
        zonename = stdout.replace(/\s+$/g, '');
        if (zonename !== 'global') {
            callback('this is not the global zone.');
            return;
        }
        callback();
    });
};
